package programming;

public class PatternExercise1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=5;i>=1;i--)
	    {
	      for(int no=1;no<=i;no++)
	      {
	        System.out.print(i+" ");
	        
	      }
	      System.out.println();

	}

}
}



